import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import { ReactMic } from "react-mic";
import { v4 as uuidv4 } from "uuid";
import { SketchCanvas } from "./components/SketchCanvas"; // ÂÅáËÆæ‰Ω†ÊúâËøô‰∏™ÁªÑ‰ª∂

/* ========= ‰∏ªÈ¢òÊ†∑Âºè ========= */
const theme = {
  userBg: "#dcf8c6",
  botBg: "#fff",
  bgPage: "#e5ddd5",
  primary: "#25d366",
  primaryHover: "#20b958",
  textPrimary: "#111b21",
  textSecondary: "#666",
  borderRadius: 20,
  bubbleMax: "75%",
  fontFamily: `"Inter", "Segoe UI", Roboto, Helvetica, Arial, sans-serif`,
  shadow: "0 4px 10px rgba(0,0,0,0.08)",
};

/* ========= Ê∂àÊÅØÊ∞îÊ≥° ========= */
function Message({ msg }) {
  const isUser = msg.role === "user";
  return (
    <div
      style={{
        display: "flex",
        justifyContent: isUser ? "flex-end" : "flex-start",
        margin: "8px 0",
        alignItems: "flex-end",
      }}
    >
      {!isUser && (
        <div
          style={{
            width: 40,
            height: 40,
            borderRadius: "50%",
            background: theme.primary,
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            color: "#fff",
            fontSize: 20,
            marginRight: 8,
            boxShadow: theme.shadow,
          }}
        >
          ü§ñ
        </div>
      )}
      <div
        style={{
          maxWidth: theme.bubbleMax,
          padding: "12px 16px",
          borderRadius: theme.borderRadius,
          background: isUser ? theme.userBg : theme.botBg,
          boxShadow: theme.shadow,
          color: theme.textPrimary,
          wordBreak: "break-word",
          whiteSpace: "pre-wrap",
          fontSize: 15,
          lineHeight: 1.5,
          position: "relative",
        }}
      >
        {msg.text}
        {msg.images?.length > 0 && (
          <div style={{ display: "flex", gap: 6, marginTop: 6 }}>
            {msg.images.map((imgObj, i) => (
              <img
                key={i}
                src={URL.createObjectURL(imgObj.file)}
                alt="pic"
                style={{
                  width: 80,
                  height: 80,
                  objectFit: "cover",
                  borderRadius: 10,
                  boxShadow: theme.shadow,
                }}
              />
            ))}
          </div>
        )}
        {msg.audio && (
          <audio
            controls
            src={URL.createObjectURL(msg.audio)}
            style={{ marginTop: 8, height: 32, width: "100%" }}
          />
        )}
      </div>
    </div>
  );
}

/* ========= ‰∏ªÁªÑ‰ª∂ ========= */
export default function ChatBot() {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const [audioBlob, setAudioBlob] = useState(null);
  const [record, setRecord] = useState(false);
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [dragOver, setDragOver] = useState(false);
  const chatEndRef = useRef(null);
  const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:5000";

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const onStop = (recordedBlob) => setAudioBlob(recordedBlob.blob);

  /* === ÂèëÈÄÅÊ∂àÊÅØ === */
  const handleSend = async () => {
    if (!text && !audioBlob && images.length === 0) return;

    const userMsg = {
      id: uuidv4(),
      role: "user",
      text: text || (audioBlob ? "üé§ Voice message" : ""),
      audio: audioBlob,
      images,
    };
    setMessages((prev) => [...prev, userMsg]);
    setText("");
    setAudioBlob(null);
    setImages([]);
    setLoading(true);

    const formData = new FormData();
    formData.append("provider_name", "openai");
    formData.append("user_text", text || "");
    if (audioBlob) formData.append("audio", audioBlob, "audio.wav");
    images.forEach((img) => formData.append("images", img.file));
    images.forEach((img) => formData.append("image_types", img.type));

    try {
      const res = await axios.post(`${API_BASE}/process_input`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      const bot = res.data?.output;

      const botMsg = {
        id: uuidv4(),
        role: "assistant",
        text: [
          bot?.fluency_understanding && `üéØ **Fluency & Understanding**\n${bot.fluency_understanding}`,
          bot?.better_expression && `üí¨ **Better Expression**\n${bot.better_expression}`,
          bot?.feedback && `üß© **Feedback**\n${bot.feedback}`,
          bot?.key_phrases && `üìö **Key Phrases**\n${bot.key_phrases}`,
          bot?.speaking_tip && `üéôÔ∏è **Speaking Tip**\n${bot.speaking_tip}`,
        ]
          .filter(Boolean)
          .join("\n\n"),
      };
      setMessages((prev) => [...prev, botMsg]);
    } catch (err) {
      console.error(err);
      alert("‚ùå Error processing message");
    } finally {
      setLoading(false);
    }
  };

  /* === ÊãñÊãΩ‰∏ä‰º† === */
  const handleDrop = (e) => {
    e.preventDefault();
    setDragOver(false);
    const files = Array.from(e.dataTransfer.files).map((file) => ({
      file,
      type: "Camera Shot üì∏",
    }));
    setImages((prev) => [...prev, ...files]);
  };

  return (
    <div
      style={{
        height: "100vh",
        display: "flex",
        flexDirection: "column",
        background: theme.bgPage,
        fontFamily: theme.fontFamily,
      }}
      onDragOver={(e) => {
        e.preventDefault();
        setDragOver(true);
      }}
      onDragLeave={() => setDragOver(false)}
      onDrop={handleDrop}
    >
      {/* È°∂Ê†è */}
      <header
        style={{
          background: theme.primary,
          color: "#fff",
          padding: "16px 24px",
          fontWeight: 600,
          fontSize: 20,
          boxShadow: theme.shadow,
        }}
      >
        üí¨ CallMe ‚Äì English Coach
      </header>

      {/* Ê∂àÊÅØÂå∫ */}
      <div
        style={{
          flex: 1,
          padding: "16px 20px 180px",
          overflowY: "auto",
          boxSizing: "border-box",
        }}
      >
        {messages.map((msg) => (
          <Message key={msg.id} msg={msg} />
        ))}
        <div ref={chatEndRef} />
      </div>

      {/* ÊãñÊãΩÈ´ò‰∫Æ */}
      {dragOver && (
        <div
          style={{
            position: "fixed",
            inset: 0,
            background: "rgba(37,211,102,0.1)",
            border: `2px dashed ${theme.primary}`,
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            fontSize: 22,
            color: theme.primary,
            zIndex: 999,
          }}
        >
          üì∏ Drop your images here
        </div>
      )}

      {/* Â∫ïÈÉ®ËæìÂÖ•Âå∫ */}
      <footer
        style={{
          position: "fixed",
          bottom: 0,
          left: 0,
          right: 0,
          background: "#fff",
          padding: "12px 20px 20px",
          display: "flex",
          alignItems: "center",
          gap: 12,
          flexWrap: "wrap",
          boxShadow: "0 -2px 8px rgba(0,0,0,0.08)",
        }}
      >
        {/* ÂΩïÈü≥ */}
        <ReactMic
          record={record}
          onStop={onStop}
          mimeType="audio/wav"
          strokeColor={theme.primary}
          backgroundColor="transparent"
          canvasStyle={{ width: 50, height: 36 }}
        />
        <button
          onClick={() => setRecord((r) => !r)}
          disabled={loading}
          style={{
            border: "none",
            background: "transparent",
            fontSize: 26,
            cursor: "pointer",
          }}
          title="Click to record"
        >
          {record ? "‚èπÔ∏è" : "üé§"}
        </button>

        {/* ÊñáÊú¨ËæìÂÖ• */}
        <input
          type="text"
          placeholder="Type your message..."
          value={text}
          onChange={(e) => setText(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && !e.shiftKey && handleSend()}
          style={{
            flex: 1,
            border: "1px solid #ddd",
            borderRadius: theme.borderRadius,
            padding: "10px 16px",
            fontSize: 15,
            outline: "none",
            background: "#fafafa",
          }}
        />

        <button
          onClick={handleSend}
          disabled={loading}
          style={{
            background: loading ? "#ccc" : theme.primary,
            color: "#fff",
            border: "none",
            borderRadius: "50%",
            width: 46,
            height: 46,
            fontSize: 20,
            cursor: loading ? "default" : "pointer",
          }}
        >
          ‚û°Ô∏è
        </button>

        {/* Sketch ÁîªÊùø */}
        <div style={{ width: "100%", marginTop: 8 }}>
          <SketchCanvas
            width={300}
            height={120}
            penColor={theme.primary}
            onSave={(imgBlob) => {
              const file = new File([imgBlob], "sketch.png", { type: "image/png" });
              setImages((prev) => [...prev, { file, type: "Manual Sketch üé®" }]);
            }}
          />
        </div>
      </footer>
    </div>
  );
}
