import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import { ReactMic } from "react-mic";
import { v4 as uuidv4 } from "uuid";

/* ---------------- ‰∏ªÈ¢òÂèòÈáè ---------------- */
const theme = {
  userBg: "#daf8cb",
  botBg: "#ffffff",
  bgPage: "#f4f7fb",
  primary: "#07c160",
  primaryHover: "#06ad56",
  textPrimary: "#222",
  textSecondary: "#666",
  borderRadius: 20,
  bubbleMax: "70%",
  fontFamily: `"Segoe UI", Roboto, Helvetica, Arial, sans-serif`,
};

/* ---------------- Ê∂àÊÅØÊ∞îÊ≥° ---------------- */
function Message({ msg }) {
  const isUser = msg.role === "user";

  return (
    <div
      style={{
        display: "flex",
        justifyContent: isUser ? "flex-end" : "flex-start",
        margin: "6px 0",
      }}
    >
      {!isUser && (
        <div
          style={{
            width: 36,
            height: 36,
            borderRadius: "50%",
            background: theme.primary,
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            color: "#fff",
            fontSize: 18,
            marginRight: 8,
            flexShrink: 0,
          }}
        >
          ü§ñ
        </div>
      )}

      <div
        style={{
          maxWidth: theme.bubbleMax,
          padding: "10px 14px",
          borderRadius: theme.borderRadius,
          background: isUser ? theme.userBg : theme.botBg,
          boxShadow: "0 1px 3px rgba(0,0,0,.08)",
          color: theme.textPrimary,
          wordBreak: "break-word",
          whiteSpace: "pre-wrap",
        }}
      >
        {msg.text}

        {msg.images?.length > 0 && (
          <div style={{ display: "flex", gap: 6, marginTop: 6 }}>
            {msg.images.map((img, i) => (
              <img
                key={i}
                src={URL.createObjectURL(img)}
                alt="pic"
                style={{
                  width: 72,
                  height: 72,
                  objectFit: "cover",
                  borderRadius: 12,
                }}
              />
            ))}
          </div>
        )}

        {msg.audio && (
          <audio
            controls
            src={URL.createObjectURL(msg.audio)}
            style={{ marginTop: 6, height: 32, outline: "none" }}
          />
        )}
      </div>

      {isUser && <div style={{ width: 36, flexShrink: 0 }} />}
    </div>
  );
}

/* ---------------- ‰∏ªÁªÑ‰ª∂ ---------------- */
export default function ChatBot() {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const [audioBlob, setAudioBlob] = useState(null);
  const [record, setRecord] = useState(false);
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [dragOver, setDragOver] = useState(false);
  const [asrId, setAsrId] = useState(null); // Êñ∞Â¢û ASR Âç†‰Ωç

  const chatEndRef = useRef(null);
  const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:5000";

  useEffect(() => chatEndRef.current?.scrollIntoView({ behavior: "smooth" }), [messages]);

  /* ===== ÂΩïÈü≥ÂÅúÊ≠¢ ===== */
  const onStop = (recordedBlob) => {
    const blob = recordedBlob.blob;
    setAudioBlob(blob);
    transcribeAudio(blob); // ‚Üí ËØÜÂà´+ÂèëÈÄÅ
  };

  /* ===== ÂΩïÈü≥‚ÜíÊñáÂ≠ó‚ÜíÂèëÈÄÅ ===== */
  const transcribeAudio = async (blob) => {
    const tempId = uuidv4();

    // ‚ë† Âç†‰ΩçÊ∂àÊÅØ
    setMessages((prev) => [
      ...prev,
      {
        id: tempId,
        role: "user",
        text: "üé§ Identifying...",
        audio: blob,
        images: [],
      },
    ]);
    setAsrId(tempId);

    // ‚ë° Ë∞É ASR
    const form = new FormData();
    form.append("audio", blob, "rec.webm");

    try {
      const res = await axios.post(`${API_BASE}/asr`, form);
      const realText = res.data?.text || "(empty)";

      // ‚ë¢ ÂéüÂú∞ÊõøÊç¢
      setMessages((prev) =>
        prev.map((m) => (m.id === tempId ? { ...m, text: realText } : m))
      );

      // ‚ë£ Ëá™Âä®ÂèëÈÄÅ
      await handleSend(realText, [], blob);
    } catch (e) {
      console.error(e);
      setMessages((prev) =>
        prev.map((m) =>
          m.id === tempId ? { ...m, text: "‚ùå Identification failed" } : m
        )
      );
    }
  };

  /* ===== ÂèëÈÄÅÊ∂àÊÅØ ===== */
  const handleSend = async (
    overrideText = text,
    overrideImages = images,
    overrideAudio = audioBlob
  ) => {
    if (!overrideText && !overrideAudio && overrideImages.length === 0) return;

    const userMsg = {
      id: uuidv4(),
      role: "user",
      text: overrideText || (overrideAudio ? "üé§ Voice" : ""),
      audio: overrideAudio,
      images: overrideImages,
    };
    setMessages((prev) => [...prev, userMsg]);

    // Ê∏ÖÁ©∫Áä∂ÊÄÅ
    if (overrideText === text) setText("");
    if (overrideImages === images) setImages([]);
    if (overrideAudio === audioBlob) setAudioBlob(null);
    setLoading(true);

    const formData = new FormData();
    formData.append("provider_name", "openai");
    formData.append("user_text", overrideText || "");
    if (overrideAudio) formData.append("audio", overrideAudio, "audio.wav");
    overrideImages.forEach((img) => formData.append("images", img));
    overrideImages.forEach(() => formData.append("image_types", "Camera Shot üì∏"));

    try {
      const res = await axios.post(`${API_BASE}/process_input`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      const bot = res.data?.output;
      const botMsg = {
        id: uuidv4(),
        role: "assistant",
        text: [
          bot?.situation && `üîñ ${bot.situation}`,
          bot?.expression && `üí° ${bot.expression}`,
          bot?.feedback && `‚úÖ ${bot.feedback}`,
          bot?.phrases && `üìö ${bot.phrases}`,
        ]
          .filter(Boolean)
          .join("\n\n"),
      };
      setMessages((prev) => [...prev, botMsg]);
    } catch (err) {
      console.error(err);
      alert("‚ùå Error processing message");
    } finally {
      setLoading(false);
    }
  };

  /* ===== ÊãñÊãΩ‰∏ä‰º† ===== */
  const handleDrop = (e) => {
    e.preventDefault();
    setDragOver(false);
    setImages(Array.from(e.dataTransfer.files));
  };

  return (
    <div
      style={{
        height: "100vh",
        display: "flex",
        flexDirection: "column",
        fontFamily: theme.fontFamily,
        background: theme.bgPage,
      }}
      onDragOver={(e) => {
        e.preventDefault();
        setDragOver(true);
      }}
      onDragLeave={() => setDragOver(false)}
      onDrop={handleDrop}
    >
      {/* È°∂ÈÉ®Ê†áÈ¢òÊ†è */}
      <header
        style={{
          padding: "14px 24px",
          fontSize: 20,
          fontWeight: 600,
          color: theme.textPrimary,
          background: "#fff",
          boxShadow: "0 2px 8px rgba(0,0,0,.04)",
          zIndex: 10,
        }}
      >
        üí¨ Call Me! English Chatbot
      </header>

      {/* Ê∂àÊÅØÂå∫ */}
      <div
        style={{
          flex: 1,
          padding: "16px 20px 80px",
          overflowY: "auto",
        }}
      >
        {messages.map((msg) => (
          <Message key={msg.id} msg={msg} />
        ))}
        <div ref={chatEndRef} />
      </div>

      {/* ÊãñÊãΩÈ´ò‰∫Æ */}
      {dragOver && (
        <div
          style={{
            position: "fixed",
            inset: 0,
            background: "rgba(7,193,96,.15)",
            border: `2px dashed ${theme.primary}`,
            pointerEvents: "none",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            fontSize: 22,
            color: theme.primary,
          }}
        >
          Drop images here
        </div>
      )}

      {/* Â∫ïÈÉ®ËæìÂÖ•Êù° */}
      <footer
        style={{
          position: "fixed",
          bottom: 0,
          left: 0,
          right: 0,
          background: "#fff",
          boxShadow: "0 -2px 8px rgba(0,0,0,.04)",
          padding: "12px 20px 20px",
        }}
      >
        <div
          style={{
            display: "flex",
            alignItems: "center",
            gap: 12,
          }}
        >
          {/* ÂΩïÈü≥Ê≥¢ÂΩ¢ */}
          <ReactMic
            record={record}
            onStop={onStop}
            mimeType="audio/wav"
            strokeColor={theme.primary}
            backgroundColor="transparent"
            canvasStyle={{ width: 50, height: 36 }}
          />

          <button
            onClick={() => setRecord((r) => !r)}
            disabled={loading}
            style={{
              border: "none",
              background: "transparent",
              fontSize: 24,
              cursor: "pointer",
            }}
            title="Hold to record"
          >
            {record ? "‚èπÔ∏è" : "üé§"}
          </button>

          {/* ÂõæÁâáÈÄâÊã© */}
