import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import { ReactMic } from "react-mic";
import { v4 as uuidv4 } from "uuid";

/* ========= ‰∏ªÈ¢òÂèòÈáè ========= */
const theme = {
  userBg: "#daf8cb",
  botBg: "#ffffff",
  bgPage: "#f0f2f5",
  primary: "#07c160",
  primaryHover: "#06ad56",
  textPrimary: "#222",
  textSecondary: "#555",
  borderRadius: 16,
  bubbleMax: "70%",
  fontFamily: `"Segoe UI", Roboto, Helvetica, Arial, sans-serif`,
  shadow: "0 4px 12px rgba(0,0,0,0.08)",
};

/* ========= Ê∂àÊÅØÊ∞îÊ≥°ÁªÑ‰ª∂ ========= */
function Message({ msg }) {
  const isUser = msg.role === "user";
  return (
    <div
      style={{
        display: "flex",
        justifyContent: isUser ? "flex-end" : "flex-start",
        margin: "8px 0",
        transition: "all 0.3s",
      }}
    >
      {!isUser && (
        <div
          style={{
            width: 36,
            height: 36,
            borderRadius: "50%",
            background: theme.primary,
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            color: "#fff",
            fontSize: 18,
            marginRight: 8,
            flexShrink: 0,
          }}
        >
          ü§ñ
        </div>
      )}
      <div
        style={{
          maxWidth: theme.bubbleMax,
          padding: "12px 16px",
          borderRadius: theme.borderRadius,
          background: isUser
            ? `linear-gradient(145deg, #daf8cb, #c2f0b8)`
            : theme.botBg,
          boxShadow: theme.shadow,
          color: theme.textPrimary,
          wordBreak: "break-word",
          whiteSpace: "pre-wrap",
          transition: "all 0.2s",
        }}
      >
        {msg.text}
        {msg.images?.length > 0 && (
          <div
            style={{
              display: "flex",
              gap: 8,
              marginTop: 8,
              flexWrap: "wrap",
            }}
          >
            {msg.images.map((imgObj, i) => (
              <img
                key={i}
                src={URL.createObjectURL(imgObj.file)}
                alt="pic"
                style={{
                  width: 80,
                  height: 80,
                  objectFit: "cover",
                  borderRadius: 12,
                  transition: "transform 0.2s",
                  cursor: "pointer",
                }}
                onMouseOver={(e) => (e.currentTarget.style.transform = "scale(1.05)")}
                onMouseOut={(e) => (e.currentTarget.style.transform = "scale(1)")}
              />
            ))}
          </div>
        )}
        {msg.audio && (
          <audio
            controls
            src={URL.createObjectURL(msg.audio)}
            style={{
              marginTop: 8,
              width: "100%",
              borderRadius: 12,
              outline: "none",
            }}
          />
        )}
      </div>
      {isUser && <div style={{ width: 36, flexShrink: 0 }} />}
    </div>
  );
}

/* ========= ‰∏ªÁªÑ‰ª∂ ========= */
export default function ChatBot() {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const [audioBlob, setAudioBlob] = useState(null);
  const [record, setRecord] = useState(false);
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [dragOver, setDragOver] = useState(false);

  const chatEndRef = useRef(null);
  const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:5000";

  useEffect(() => chatEndRef.current?.scrollIntoView({ behavior: "smooth" }), [messages]);

  const onStop = (recordedBlob) => setAudioBlob(recordedBlob.blob);

  const handleSend = async () => {
    if (!text && !audioBlob && images.length === 0) return;

    const userMsg = {
      id: uuidv4(),
      role: "user",
      text: text || (audioBlob ? "üé§ Voice" : ""),
      audio: audioBlob,
      images,
    };
    setMessages((prev) => [...prev, userMsg]);
    setText("");
    setAudioBlob(null);
    setImages([]);
    setLoading(true);

    const formData = new FormData();
    formData.append("provider_name", "openai");
    formData.append("user_text", text || "");
    if (audioBlob) formData.append("audio", audioBlob, "audio.wav");
    images.forEach((img) => formData.append("images", img.file));
    images.forEach((img) => formData.append("image_types", img.type));

    try {
      const res = await axios.post(`${API_BASE}/process_input`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      const bot = res.data?.output;

      const botMsg = {
        id: uuidv4(),
        role: "assistant",
        text: [
          bot?.fluency_understanding && `üéØ Fluency & Understanding\n${bot.fluency_understanding}`,
          bot?.better_expression && `üí¨ Better Expression\n${bot.better_expression}`,
          bot?.feedback && `üß© Feedback\n${bot.feedback}`,
          bot?.key_phrases && `üìö Key Phrases\n${bot.key_phrases}`,
          bot?.speaking_tip && `üéôÔ∏è Speaking Tip\n${bot.speaking_tip}`,
        ]
          .filter(Boolean)
          .join("\n\n"),
      };
      setMessages((prev) => [...prev, botMsg]);
    } catch (err) {
      console.error("‚ùå Error in handleSend:", err);
      alert("‚ùå Error processing message");
    } finally {
      setLoading(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setDragOver(false);
    const files = Array.from(e.dataTransfer.files).map((file) => ({
      file,
      type: "Camera Shot üì∏",
    }));
    setImages((prev) => [...prev, ...files]);
  };

  return (
    <div
      style={{
        height: "100vh",
        display: "flex",
        flexDirection: "column",
        fontFamily: theme.fontFamily,
        background: theme.bgPage,
      }}
      onDragOver={(e) => {
        e.preventDefault();
        setDragOver(true);
      }}
      onDragLeave={() => setDragOver(false)}
      onDrop={handleDrop}
    >
    {/* È°∂ÈÉ®Ê†áÈ¢òÊ†è */}
    <header
      style={{
        padding: "16px 24px",
        fontSize: 24,
