            }}
          >
            {record ? "‚èπÔ∏è" : "üé§"}
          </button>

          {/* ÂõæÁâá‰∏ä‰º† */}
          {["üì∑", "üé®"].map((icon, idx) => (
            <label
              key={idx}
              style={{
                fontSize: 22,
                cursor: "pointer",
                padding: "6px",
                borderRadius: 12,
                background: "#f0f0f0",
                transition: "all 0.2s",
              }}
              onMouseEnter={(e) => (e.currentTarget.style.background = "#e0e0e0")}
              onMouseLeave={(e) => (e.currentTarget.style.background = "#f0f0f0")}
            >
              {icon}
              <input
                type="file"
                multiple
                accept="image/*"
                onChange={(e) => {
                  const files = Array.from(e.target.files).map((file) => ({
                    file,
