                >
                  <option value="Camera Shot üì∏">Camera Shot üì∏</option>
                  <option value="Manual Sketch üé®">Manual Sketch üé®</option>
                </select>
                <button
                  onClick={() => setImages(images.filter((_, i) => i !== idx))}
                  style={{
                    background: "#f44336",
                    color: "#fff",
                    border: "none",
                    borderRadius: 6,
                    padding: "4px 8px",
                    cursor: "pointer",
                  }}
