import React, { useState, useRef } from "react";
import axios from "axios";
import { ReactMic } from "react-mic";
import { v4 as uuidv4 } from "uuid";

function MessageBubble({ message }) {
  return (
    <div style={{ textAlign: message.role === "user" ? "right" : "left", margin: "0.5rem 0" }}>
      <div
        style={{
          display: "inline-block",
          background: message.role === "user" ? "#DCF8C6" : "#fff",
          padding: "0.8rem 1rem",
          borderRadius: "12px",
          maxWidth: "75%",
          whiteSpace: "pre-wrap",
        }}
      >
        {message.text}
        {message.images?.map((img, i) => (
          <img
            key={i}
            src={URL.createObjectURL(img)}
            alt="uploaded"
            style={{ width: 100, borderRadius: "6px", margin: "0.3rem" }}
          />
        ))}
      </div>
    </div>
  );
}

function App() {
  const [messages, setMessages] = useState([]);
  const [userText, setUserText] = useState("");
  const [record, setRecord] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null);
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const chatBoxRef = useRef(null);

  const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:5000";

  const scrollToBottom = () => {
    chatBoxRef.current?.scrollTo({ top: chatBoxRef.current.scrollHeight, behavior: "smooth" });
  };

  const startRecording = () => setRecord(true);
  const stopRecording = () => setRecord(false);

  const onStop = async (recordedBlob) => {
    setAudioBlob(recordedBlob.blob);

    const formData = new FormData();
    formData.append("audio", recordedBlob.blob, "audio.wav");

    try {
      const res = await axios.post(`${API_BASE}/speech2text`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setUserText(res.data.transcript || "");
    } catch (err) {
      console.error(err);
      alert("Speech recognition failed.");
    }
  };

  const handleImageChange = (e) => setImages(Array.from(e.target.files));

  const sendMessage = async () => {
    if (!userText && !audioBlob && images.length === 0) return;

    const newMsg = { id: uuidv4(), role: "user", text: userText || "(üé§ Voice message)", images };
    setMessages((prev) => [...prev, newMsg]);
    setUserText(""); 
    setAudioBlob(null); 
    setImages([]);
    setLoading(true);

    const formData = new FormData();
    formData.append("provider_name", "openai");
    formData.append("user_text", userText || "");
    if (audioBlob) formData.append("audio", audioBlob, "audio.wav");
    images.forEach((img) => formData.append("images", img));
    images.forEach(() => formData.append("image_types", "Camera Shot üì∏"));

    try {
      const res = await axios.post(`${API_BASE}/process_input`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      const botReply = res.data?.output;
      const assistantMsg = {
        id: uuidv4(),
        role: "assistant",
        text: [
          botReply?.situation && `üîñ ${botReply.situation}`,
          botReply?.expression && `üí° ${botReply.expression}`,
          botReply?.feedback && `‚úÖ ${botReply.feedback}`,
          botReply?.phrases && `üìö ${botReply.phrases}`,
        ].filter(Boolean).join("\n\n"),
      };
      setMessages((prev) => [...prev, assistantMsg]);
      scrollToBottom();
    } catch (err) {
      console.error(err);
      alert("‚ùå Failed to process input. Check backend logs.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: 800, margin: "2rem auto", fontFamily: "sans-serif" }}>
      <h1>üí¨ Call Me! ‚Äì English Chatbot</h1>

      <div
        ref={chatBoxRef}
        style={{
          border: "1px solid #ccc",
          borderRadius: "10px",
          padding: "1rem",
          height: "60vh",
          overflowY: "auto",
          background: "#fafafa",
        }}
      >
        {messages.map((msg) => <MessageBubble key={msg.id} message={msg} />)}
      </div>

      <div style={{ marginTop: "1rem" }}>
        <textarea
          rows={2}
          value={userText}
          onChange={(e) => setUserText(e.target.value)}
          placeholder="Type your message or record your voice..."
          style={{ width: "100%", borderRadius: "8px", padding: "0.5rem" }}
        />

        <div style={{ marginTop: "0.5rem" }}>
          <ReactMic
            record={record}
            className="sound-wave"
            onStop={onStop}
            mimeType="audio/wav"
            strokeColor="#000000"
            backgroundColor="#FF4081"
          />
          <button onClick={startRecording} disabled={record || loading}>üéôÔ∏è Start</button>
          <button onClick={stopRecording} disabled={!record || loading}>‚èπÔ∏è Stop</button>

          <input type="file" accept="image/*" multiple onChange={handleImageChange} style={{ marginLeft: "1rem" }} />
          <button onClick={sendMessage} disabled={loading} style={{ marginLeft: "1rem" }}>
            {loading ? "Thinking..." : "Send üí¨"}
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
