import React, { useState, useEffect } from "react";
import axios from "axios";
import { ReactMic } from "react-mic"; // npm install react-mic

function App() {
  const [provider, setProvider] = useState("openai");
  const [providers, setProviders] = useState([]);
  const [record, setRecord] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null);
  const [transcript, setTranscript] = useState("");
  const [userText, setUserText] = useState("");
  const [images, setImages] = useState([]);
  const [imageTypes, setImageTypes] = useState([]);
  const [result, setResult] = useState(null);

  useEffect(() => {
    axios.get("http://localhost:5000/providers").then((res) => {
      setProviders(res.data);
    });
  }, []);

  const startRecording = () => setRecord(true);
  const stopRecording = () => setRecord(false);

  const onStop = (recordedBlob) => {
    setAudioBlob(recordedBlob.blob);
    const formData = new FormData();
    formData.append("audio", recordedBlob.blob, "audio.wav");
    formData.append("provider_name", provider);
    axios
      .post("http://localhost:5000/speech2text", formData)
      .then((res) => {
        setTranscript(res.data.transcript);
        setUserText(res.data.transcript);
      })
      .catch((err) => console.error(err));
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    setImages(files);
    setImageTypes(files.map(() => "Camera Shot üì∏")); // ÈªòËÆ§Á±ªÂûã
  };

  const handleImageTypeChange = (index, type) => {
    const newTypes = [...imageTypes];
    newTypes[index] = type;
    setImageTypes(newTypes);
  };

  const handleSubmit = () => {
    const formData = new FormData();
    formData.append("provider_name", provider);
    formData.append("user_text", userText);
    images.forEach((img) => formData.append("images", img));
    imageTypes.forEach((type) => formData.append("image_types", type));

    axios
      .post("http://localhost:5000/process_input", formData)
      .then((res) => setResult(res.data))
      .catch((err) => console.error(err));
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h1>‚õÑÔ∏è Call Me! ‚Äì Learning English Together</h1>

      <label>Choose AI provider: </label>
      <select value={provider} onChange={(e) => setProvider(e.target.value)}>
        {providers.map((p) => (
          <option key={p} value={p}>
            {p}
          </option>
        ))}
      </select>

      <hr />
      <h2>Step 1: üé§ Record your voice</h2>
      <ReactMic
        record={record}
        className="sound-wave"
        onStop={onStop}
        strokeColor="#000000"
        backgroundColor="#FF4081"
      />
      <button onClick={startRecording}>Start Recording</button>
      <button onClick={stopRecording}>Stop Recording</button>

      {transcript && (
        <div>
          <h3>Transcript:</h3>
          <textarea
            rows={3}
            value={userText}
            onChange={(e) => setUserText(e.target.value)}
          />
        </div>
      )}

      <hr />
      <h2>Step 2: üñºÔ∏è Upload images</h2>
      <input type="file" multiple onChange={handleImageChange} />
      {images.map((img, idx) => (
        <div key={idx}>
          <img
            src={URL.createObjectURL(img)}
            alt="preview"
            width={150}
            style={{ marginRight: "1rem" }}
          />
          <select
            value={imageTypes[idx]}
            onChange={(e) => handleImageTypeChange(idx, e.target.value)}
          >
            <option>Camera Shot üì∏</option>
            <option>Manual Sketch üé®</option>
          </select>
        </div>
      ))}

      <hr />
      <button onClick={handleSubmit}>Submit</button>

      {result && (
        <div style={{ marginTop: "2rem" }}>
          <h2>Results:</h2>
          {result.output?.situation && (
            <div>
              <h3>üîñ Recap the scenario</h3>
              <p>{result.output.situation}</p>
            </div>
          )}
          {result.output?.expression && (
            <div>
              <h3>üí° Better English Expression</h3>
              <p>{result.output.expression}</p>
            </div>
          )}
          {result.output?.feedback && (
            <div>
              <h3>‚úÖ Feedback on Your Attempt</h3>
              <p>{result.output.feedback}</p>
            </div>
          )}
          {result.output?.phrases && (
            <div>
              <h3>üìö Useful Phrases</h3>
              <p>{result.output.phrases}</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
