import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import { ReactMic } from "react-mic";
import { v4 as uuidv4 } from "uuid";

function Message({ msg }) {
  const isUser = msg.role === "user";
  return (
    <div style={{ display: "flex", justifyContent: isUser ? "flex-end" : "flex-start", margin: "0.5rem 0" }}>
      <div
        style={{
          maxWidth: "70%",
          padding: "0.7rem 1rem",
          borderRadius: 24,
          background: isUser ? "#DCF8C6" : "#F1F0F0",
          whiteSpace: "pre-wrap",
          wordBreak: "break-word",
        }}
      >
        {msg.text}
        {msg.images?.map((img, i) => (
          <img key={i} src={URL.createObjectURL(img)} alt="uploaded" style={{ width: 100, borderRadius: 12, marginTop: 4 }} />
        ))}
        {msg.audio && <audio controls src={URL.createObjectURL(msg.audio)} style={{ marginTop: 4 }} />}
      </div>
    </div>
  );
}

export default function ChatBot() {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const [audioBlob, setAudioBlob] = useState(null);
  const [record, setRecord] = useState(false);
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);

  const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:5000";

  const scrollToBottom = () => chatEndRef.current?.scrollIntoView({ behavior: "smooth" });

  useEffect(scrollToBottom, [messages]);

  const onStop = (recordedBlob) => setAudioBlob(recordedBlob.blob);

  const handleSend = async () => {
    if (!text && !audioBlob && images.length === 0) return;

    const userMsg = { id: uuidv4(), role: "user", text: text || "(üé§ Voice)", audio: audioBlob, images };
    setMessages((prev) => [...prev, userMsg]);
    setText(""); setAudioBlob(null); setImages([]);
    setLoading(true);

    const formData = new FormData();
    formData.append("provider_name", "openai");
    formData.append("user_text", text || "");
    if (audioBlob) formData.append("audio", audioBlob, "audio.wav");
    images.forEach(img => formData.append("images", img));
    images.forEach(() => formData.append("image_types", "Camera Shot üì∏"));

    try {
      const res = await axios.post(`${API_BASE}/process_input`, formData, { headers: { "Content-Type": "multipart/form-data" } });
      const bot = res.data?.output;
      const botMsg = {
        id: uuidv4(),
        role: "assistant",
        text: [
          bot?.situation && `üîñ ${bot.situation}`,
          bot?.expression && `üí° ${bot.expression}`,
          bot?.feedback && `‚úÖ ${bot.feedback}`,
          bot?.phrases && `üìö ${bot.phrases}`
        ].filter(Boolean).join("\n\n")
      };
      setMessages(prev => [...prev, botMsg]);
    } catch (err) {
      console.error(err);
      alert("‚ùå Error processing message");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", height: "100vh", fontFamily: "sans-serif", background: "#f9f9f9" }}>
      <header style={{ padding: 16, fontSize: 24, fontWeight: 600 }}>üí¨ Call Me! English Chatbot</header>

      <div style={{ flex: 1, padding: 16, overflowY: "auto" }}>
        {messages.map(msg => <Message key={msg.id} msg={msg} />)}
        <div ref={chatEndRef} />
      </div>

      <div style={{ display: "flex", padding: 12, borderTop: "1px solid #ddd", background: "#fff", gap: 8, alignItems: "center" }}>
        <ReactMic
          record={record}
          className="sound-wave"
          onStop={onStop}
          mimeType="audio/wav"
          strokeColor="#000000"
          backgroundColor="#FF4081"
          style={{ width: 50, height: 50 }}
        />
        <button onClick={() => setRecord(true)} disabled={record || loading}>üé§</button>
        <button onClick={() => setRecord(false)} disabled={!record || loading}>‚èπÔ∏è</button>

        <input type="file" multiple onChange={(e) => setImages(Array.from(e.target.files))} />

        <input
          type="text"
          placeholder="Type or record..."
          value={text}
          onChange={(e) => setText(e.target.value)}
          style={{ flex: 1, borderRadius: 24, padding: "0.5rem 1rem", border: "1px solid #ccc" }}
          onKeyDown={(e) => e.key === "Enter" && handleSend()}
        />
        <button onClick={handleSend} disabled={loading}>‚û°Ô∏è</button>
      </div>
    </div>
  );
}
